import React from 'react'
import CartContext from './CartContext'

const CartProvider = (props) => {
    if(cartCtx.showCart){
        console.log('CP')
    }
    const cartCtx={
        items:[],
        showCart:false
    }
  return (
    <CartContext.Provider value={cartCtx}>
      {props.children}
    </CartContext.Provider>
  )
}

export default CartProvider
