import React,{useState} from 'react'
import CartContext from './CartContext'

const CartProvider = (props) => {
    const productsArr=[[
      {
        Id:1,
        title: "Colors",
        price: 100,
        imageUrl:
          "https://prasadyash2411.github.io/ecom-website/img/Album%201.png",
          Qty:0
      },
      {
        Id:2,
        title: "Black and white Colors",
        price: 50,
        imageUrl:
          "https://prasadyash2411.github.io/ecom-website/img/Album%202.png",
          Qty:0
      },
      {
        Id:3,
        title: "Yellow and Black Colors",
        price: 70,
        imageUrl:
          "https://prasadyash2411.github.io/ecom-website/img/Album%203.png",
          Qty:0
      },
      {
        Id:4,
        title: "Blue Color",
        price: 100,
        imageUrl:
          "https://prasadyash2411.github.io/ecom-website/img/Album%204.png",
          Qty:0
      },
    
  ]]
    const[cartOpen,setCartOpen]=useState(false)
    const[data,setData]=useState(productsArr)
    function showCartFunctionHAndler(){
        console.log(data);
        setCartOpen(true)
    }
    function addItemToCartHandler(id) {
      const addItem=data.filter((item)=>{
        return item.Id===Number(id) && (item.Qty=item.Qty+1)
      })
      setData([...data,addItem])
    }
    const cartCtx={
        items:data,
        showCart:showCartFunctionHAndler,
        openCart:cartOpen,
        addItemToCart:addItemToCartHandler
    }
  return (
    <CartContext.Provider value={cartCtx}>
      {props.children}
    </CartContext.Provider>
  )
}

export default CartProvider
