import React from 'react';
import ReactDOM from 'react-dom'
import './Form.css'
const Backdrop = (props) => {
    return <div className='backdrop' onClick={errorMessage}></div>
}
const Error=(props)=>{
    return (
        <div className='error'>
            <header><h2>{props.title}</h2></header>
            <p>{props.message}</p>
            <footer>
                <button type="button" onClick={errorMessage}>OKAY</button>
            </footer>
        </div>
  )
}
function errorMessage(props){
    props.onErrorMessage()
}
const ErrorModel = (props) => {
    return(
        <React.Fragment>
            {ReactDOM.createPortal(<Backdrop onClick={errorMessage}/>,document.getElementById('Backdrop-root'))}
            {ReactDOM.createPortal(<Error title={props.title} message={props.message} />,document.getElementById('Error-root'))}
        </React.Fragment>
    ) 
  
}

export default ErrorModel
