import React, {  useContext, useEffect} from "react";
import { Container, Navbar, NavbarBrand } from "react-bootstrap";
// import { Link } from "react-router-dom/cjs/react-router-dom";
import { NavLink } from "react-router-dom/cjs/react-router-dom";
import CartContext from "./Context/CartContext";
const Header = () => {
  const ctx=useContext(CartContext)
  function logout(){
    ctx.loadFromCrud(ctx.items)
    ctx.isLoggedInFunction(null)
    localStorage.setItem('Token',null)
  }
  useEffect(()=>{
    if(localStorage.getItem('Token'==='null')){
      ctx.isLoggedInFunction(null)
    }else{
      ctx.isLoggedInFunction(localStorage.getItem('Token'))
    }

    // eslint-disable-next-line
  },[])
  return (
    <Navbar
      bg="dark"
      expand="sm"
      variant="dark"
      className="position-fixed w-100 text-l"
      style={{ zIndex: "5" }}
    >
      <Container >
        <NavLink to="/HOME"><NavbarBrand>HOME</NavbarBrand></NavLink>
        <NavLink to="/STORE"><NavbarBrand>STORE</NavbarBrand></NavLink>
        <NavLink to="/ABOUT"><NavbarBrand>ABOUT</NavbarBrand></NavLink>
        <NavLink to="/CONTACT"><NavbarBrand>CONTACT US</NavbarBrand></NavLink>
        {!ctx.isLoggedIn&&<NavLink to="/LOGIN"><NavbarBrand>LOGIN</NavbarBrand></NavLink>}
        {ctx.isLoggedIn&&<NavLink to="/LOGIN"><NavbarBrand onClick={logout}>LOGOUT</NavbarBrand></NavLink>}
      </Container>
    </Navbar>
  );
};

export default Header;
