import React,{useContext} from 'react'
import { useParams } from 'react-router-dom'
import CartContext from '../Context/CartContext'
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
const ProductDetail = () => {
    const details=useContext(CartContext)
    const params=useParams()
    const key=Number(params.Id)
    for(const item in details.items){
        if(item.Id===key){
            console.log('oo ooo ooo o o oooo ooo ')
        }
    }
  return (
    <Card className='container' style={{ width: '75%' ,position:'fixed',top:'5rem'}}>
      {/* <Card.Img variant="top" src={detailed.imageUrl} /> */}
      <Card.Body>
        {/* <Card.Title>{detailed.title}</Card.Title> */}
        <Card.Text>
        <i class="fa-solid fa-star"></i><i class="fa-solid fa-star"></i><i class="fa-solid fa-star"></i><i class="fa-solid fa-star"></i><i class="fa-solid fa-star-half"></i> 
        </Card.Text>
        <Button variant="primary">ADD TO CART</Button>
        <Button variant="danger">GO BACK</Button>
      </Card.Body>
    </Card>
  )
}

export default ProductDetail
