import React, { useContext } from 'react'
import CartButton from'./CartButton'
import ShoeInput from './ShoeInput'
import ShoeDisplay from './ShoeDisplay'
import CartDisplay from './CartDisplay'
import ShoeContext from './Context/ShoeContext'

const MainHome = () => {
  const details=useContext(ShoeContext)
  return (
    <div>
      <header>
        <h2>SHOE WEBSITE</h2>
        <CartButton/>
      </header>
      <main>
        <ShoeInput/>
        <hr/>
        <h3>STOCK</h3>
        <ShoeDisplay/>
        <hr/>
        <h3>CART</h3>
        {details.items.filter((item)=>{
          if(item.Qty>0){
            return <CartDisplay details={item}/>
          }
        })}
      </main>
    </div>
  )
}

export default MainHome
