import { ReactDOM } from 'react'
import './Form.css'
function errorMessage(props){
    props.onErrorMessage()
}

const Backdrop = () => {
    return <div className='backdrop' onClick={errorMessage}></div>
}
const Error=(props)=>{
    return (
        <div className='error'>
            <header><h2>{props.title}</h2></header>
            <p>{props.message}</p>
            <footer>
                <button type="button" onClick={errorMessage}>OKAY</button>
            </footer>
        </div>
  )
}
const ErrorModel = (props) => {
    return(
        <React.Fragment>
            {ReactDOM.createPortal(<Backdrop/>,document.getElementById('Backdrop-root'))}
            {ReactDOM.createPortal(<Error/>,document.getElementById('Error-root'))}
        </React.Fragment>
    ) 
  
}

export default ErrorModel
