import React, { useState } from 'react'
import SubmittedDetails from './SubmittedDetails'
const UserForm = () => {
    const[oldName,updateName]=useState('')
    const[oldAge,updateAge]=useState('')
    function updatingName(names) {
        updateName(names)
    }
    function updatingAge(age) {
        updateAge(age)
    }

    function submitDetails(e) {
        {e.preventDefault()}
        <SubmittedDetails Name={oldName} Age={oldAge}/>
    }
    return (
        <form onSubmit={submitDetails}>
            <label>Username</label>
            <input type='text' onChange={updatingName}/>
            <label>Age</label>
            <input type='number' min='1' onChange={updatingAge}/>
            <button type='submit'>SUBMIT</button>
        </form>
    )
}

export default UserForm
