import React,{useState} from 'react'
import CartContext from './CartContext'
import axios from 'axios'

const CartProvider = (props) => {
    const[cartOpen,setCartOpen]=useState(false)
    const[login,setLogin]=useState(null)
    const[data,setData]=useState([
        {
          Id:1,
          title: "Colors",
          price: 100,
          imageUrl:
            "https://prasadyash2411.github.io/ecom-website/img/Album%201.png",
            Qty:'0'
        },
        {
          Id:2,
          title: "Black and white Colors",
          price: 50,
          imageUrl:
            "https://prasadyash2411.github.io/ecom-website/img/Album%202.png",
            Qty:'0'
        },
        {
          Id:3,
          title: "Yellow and Black Colors",
          price: 70,
          imageUrl:
            "https://prasadyash2411.github.io/ecom-website/img/Album%203.png",
            Qty:'0'
        },
        {
          Id:4,
          title: "Blue Color",
          price: 100,
          imageUrl:
            "https://prasadyash2411.github.io/ecom-website/img/Album%204.png",
            Qty:'0'
        },
      
    ])
    function showCartHandler(){
        if(cartOpen){
          setCartOpen(false)
        }else{
          setCartOpen(true)
        }
    }
    async function addItemToCartHandler(id) {

      const addItem=data.filter((item)=>{
        if(item.Id===Number(id)){
          return item.Qty=Number(item.Qty)+1
        }
        return item
        
      })
      setData(addItem)
      crudcrud(addItem)
    }
    function removeItemFromCartHandler(id) {
      const removeItem=data.filter((item)=>{
        if(item.Id===Number(id)){
          return item.Qty='0'
        }
        return item
        
      })
      setData(removeItem)
      crudcrud(removeItem)
    }
    async function crudcrud(cart) {
      const storage=await axios.get(`https://crudcrud.com/api/ba12496deaaf450bb707a1453f84441f/P1`)
      try{
        if(storage.data.length===0){
          const response=await axios.post(`https://crudcrud.com/api/ba12496deaaf450bb707a1453f84441f/P1`,cart)
          try {
            console.log(response.data)
            localStorage.setItem('CrudID',response.data._id)
          } catch (error) {
            console.log('error');
          }
        }
        else{
          const del=await axios.delete(`https://crudcrud.com/api/ba12496deaaf450bb707a1453f84441f/P1`)
          try{
            console.log(del)
            const response=await axios.post(`https://crudcrud.com/api/ba12496deaaf450bb707a1453f84441f/P1`,cart)
            try {
              console.log(response.data)
              localStorage.setItem('CrudID',response.data._id)
            } catch (error) {
              console.log('error');
            }
          }catch{
            console.log('error in else response')
          }

        }
      }catch{
        console.log('error in storage')
      }
      
    }
    function isLoggedInFunctionHandler(token) {
      setLogin(token)
    }
    const cartCtx={
        items:data,
        showCart:showCartHandler,
        openCart:cartOpen,
        addItemToCart:addItemToCartHandler,
        removeItemFromCart:removeItemFromCartHandler,
        isLoggedIn:login,
        isLoggedInFunction:isLoggedInFunctionHandler
    }
  return (
    <CartContext.Provider value={cartCtx}>
      {props.children}
    </CartContext.Provider>
  )
}

export default CartProvider
