import ReactDom from 'react-dom'
import './Form.css'
function errorHere(){
    props.onErrorMessage()
}
const Backdrop = (props) => {
    return <div className='backdrop' onClick={errorHere}></div>
}
const Error=(props)=>{
    return (
        <div className='error'>
            <header><h2>{props.title}</h2></header>
            <p>{props.message}</p>
            <footer>
                <button type="button" onClick={errorHere}>OKAY</button>
            </footer>
        </div>
    )    
}
const ErrorModel = (props) => {
    return(
        <>
            {ReactDom.createPortal(<Backdrop onClick={errorHere}/>,document.getElementById('Backdrop-root'))}
            {ReactDom.createPortal(<Error title={props.title} message={props.message} onClick={errorHere} />,document.getElementById('Error-root'))}
        </>
    ) 
  
}

export default ErrorModel
