import React, { useState } from 'react'
import CartButton from'./CartButton'
import ShoeInput from './ShoeInput'
import ShoeDisplay from './ShoeDisplay'
import CartDisplay from './CartDisplay'

const MainHome = () => {
  const[cart,updateCart]=useState(false)
  function showCart(){
    if(cart){
      updateCart(false)
    }else{
      updateCart(true)
    }
  }
  return (
    <div>
      <header>
        <h2>SHOE WEBSITE</h2>
        <CartButton showCart={showCart}/>
      </header>
      <main>
        <ShoeInput/>
        <hr/>
        <h3>STOCK</h3>
        <ShoeDisplay />
        <hr/>
        <h3>CART</h3>
        {cart&&<CartDisplay/>}
      </main>
    </div>
  )
}

export default MainHome
