import React, { useContext,  } from "react";
import { Card, Button,  } from "react-bootstrap";
import CartContext from "../Context/CartContext";

const Product = () => {
  const productsArr = [
    {
      Id:1,
      title: "Colors",
      price: 100,
      imageUrl:
        "https://prasadyash2411.github.io/ecom-website/img/Album%201.png",
        Qty:0
    },
    {
      Id:2,
      title: "Black and white Colors",
      price: 50,
      imageUrl:
        "https://prasadyash2411.github.io/ecom-website/img/Album%202.png",
        Qty:0
    },
    {
      Id:3,
      title: "Yellow and Black Colors",
      price: 70,
      imageUrl:
        "https://prasadyash2411.github.io/ecom-website/img/Album%203.png",
        Qty:0
    },
    {
      Id:4,
      title: "Blue Color",
      price: 100,
      imageUrl:
        "https://prasadyash2411.github.io/ecom-website/img/Album%204.png",
        Qty:0
    },
  ];
  const details=useContext(CartContext)
  // useEffect(()=>{
  //   details.add
  // },0)
  function addItemToCart(e){
    const addItem=productsArr.filter((item)=>{
      console.log(item,e.target.parentElement.parentElement.id)
      return item.Id===Number(e.target.parentElement.parentElement.id) && (item.Qty=item.Qty+1)
    })
    console.log(addItem)
    details.addItemToCart(addItem)
  }
  
  return (
    <div className="container"  style={{maxWidth:'67vw',padding:'20px 30px',margin:'auto',}}>
      <div className="d-flex justify-content-around flex-wrap" style={{rowGap:'2rem'}}>
        {productsArr.map((item) => {
          return (
              <Card id={item.Id} style={{ width: "18rem"}}>
                <Card.Title className="text-center">{item.title}</Card.Title>
                <Card.Img variant="top" src={item.imageUrl} />
                <Card.Body className='d-flex align-items-baseline justify-content-between'>
                  <Card.Text className="">${item.price}</Card.Text>
                  <Button variant="primary" onClick={addItemToCart}>ADD TO CART</Button>
                </Card.Body>
              </Card>
        )})}
      </div>
    </div>
  );
};

export default Product;
