import React, { useContext } from "react";
import { useParams } from "react-router-dom";
import CartContext from "../Context/CartContext";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
import { Badge } from "react-bootstrap";
import CartDisplay from "./CartDisplay";
const ProductDetail = () => {
  const details = useContext(CartContext);
  const { Id } = useParams();
  return (
    <>
      <Button
        variant="primary"
        style={{ zIndex: "6", position: "fixed", right: "1%", top: "0%" }}
        onClick={details.showCart}
      >
        Cart{" "}
        <Badge bg="secondary">
          {details.items.reduce((acc, curr) => {
            return acc + Number(curr.Qty);
          }, 0)}
        </Badge>
      </Button>
      {details.openCart && <CartDisplay />}
      <div
        className="container"
        style={{
          maxWidth: "67vw",
          padding: "20px 30px",
          margin: "auto",
          position: "absolute",
          top: "8rem",
        }}
      >
        {details.items.filter((item) => {
          return (
            Number(item.Id) === Number(Id) && (
              <Card style={{ width: "18rem" }}>
                <Card.Img variant="top" src={item.imageUrl}/>
                <Card.Body>
                  <Card.Title>Card Title</Card.Title>
                  <Card.Text>
                    Some quick example text to build on the card title and make
                    up the bulk of the card's content.
                  </Card.Text>
                  <Button variant="primary">Go somewhere</Button>
                </Card.Body>
              </Card>
            )
          );
        })}
      </div>
    </>
  );
};

export default ProductDetail;
