import React from 'react'
import CartContext from './CartContext'

const CartProvider = (props) => {
    const[data,setData]=useState(false)
    if(data){
        console.log("CP");
    }
    const cartCtx={
        items:[],
        showCart:data
    }
  return (
    <CartContext.Provider value={cartCtx}>
      {props.children}
    </CartContext.Provider>
  )
}

export default CartProvider
