import React, { useContext } from "react";
import { useParams } from "react-router-dom";
import CartContext from "../Context/CartContext";
import Button from "react-bootstrap/Button";
import Card from "react-bootstrap/Card";
const ProductDetail = () => {
  const details = useContext(CartContext);
  const params = useParams();
  console.log(params.Id);
  const detailed=details.items.find((item)=>item.Id===params)
  return (
    <Card
      className="container"
      style={{ width: "75%", position: "absolute", top: "5rem" }}
    >
      <Card.Img variant="top" src={detailed.imageUrl} />
      <Card.Body>
        <Card.Title>{detailed.title}</Card.Title>
        <Card.Text>
          <i class="fa-solid fa-star"></i>
          <i class="fa-solid fa-star"></i>
          <i class="fa-solid fa-star"></i>
          <i class="fa-solid fa-star"></i>
          <i class="fa-solid fa-star-half"></i>
        </Card.Text>
        <Button variant="primary">ADD TO CART</Button>
        <Button variant="danger">GO BACK</Button>
      </Card.Body>
    </Card>
  );
};

export default ProductDetail;
